mapper.select:
SELECT id,mid,dishes,price,quantity,orderid,status,DATE_FORMAT(createtime,"%Y-%m-%d %H:%m") as createtime FROM  s WHERE s.status != 9
and s.id = #{id}
and s.mid = #{mid}
and s.dishes = #{dishes}
and s.price = #{price}
and s.quantity = #{quantity}
and s.orderid = #{orderid}

SELECT id,mid,dishes,price,quantity,orderid,status,DATE_FORMAT(createtime,"%Y-%m-%d %H:%m") as createtime FROM  s WHERE s.status != 9
and s.dishes = #{dishes}
and s.price = #{price}
and s.quantity = #{quantity}
and s.orderid = #{orderid}

mapper.update:
UPDATE  s SET status=1
,s.mid = #{mid}
,s.dishes = #{dishes}
,s.price = #{price}
,s.quantity = #{quantity}

,s.status = #{status}
WHERE status != 9 and s.id = #{id}
