mapper.update:
update   set status=1
,dishes=#{dishes}
,price=#{price}
,picture=#{picture}
,typeid=#{typeid}
,status=#{status}
where status!=9 and id=#{id}

mapper.select:
SELECT id,dishes,price,picture,typeid,hid,status,DATE_FORMAT(createtime,"%Y-%m-%d %H:%m") as createtime FROM  s WHERE s.status!=9
and s.dishes=#{dishes}
and s.price=#{price}
and s.picture=#{picture}
and s.typeid=#{typeid}
and s.hid=#{hid}
and s.status=#{status}
