mapper.update:
update  s set s.status=1
,s.wxaccount=#{wxaccount}
,s.tablenumber=#{tablenumber}
,s.money=#{money}
,s.status=#{status}
where s.status!=9 and s.id=#{id}

mapper.select:
SELECT id,wxaccount,tablenumber,money,status,DATE_FORMAT(createtime,"%Y-%m-%d %H:%m") as createtime FROM  o WHERE o.status!=9
and o.tablenumber=#{tablenumber}
and o.createtime=#{createtime}
and o.status=#{status}

SELECT id,wxaccount,tablenumber,money,status,DATE_FORMAT(createtime,"%Y-%m-%d %H:%m") as createtime FROM  o WHERE o.status!=9
and date(o.createtime)= curdate()
and o.tablenumber = #{tablenumber}
and o.wxaccount = #{wxaccount}
